package com.apollocurrency.aplwallet.apl.tools;

import com.apollocurrency.aplwallet.apl.util.Convert;
import org.junit.Assert;
import org.junit.Test;

import java.io.ByteArrayOutputStream;
import java.io.PrintStream;

public class RSADecryptionTest {

    @Test
    public void testSingleDecryption() throws Exception {
        PrintStream systemOut = System.out;
        ByteArrayOutputStream out = new ByteArrayOutputStream(1024 * 1024);
        try {
            PrintStream printStream = new PrintStream(out);
            System.setOut(printStream);
            String[] params = new String[] {
 ""                   ,"certs/1_2.crt"
                    ,"false"
            };

            RSADecryption.main(params);
            printStream.close();
            String consoleOutput = new String(out.toByteArray());
            Assert.assertTrue(consoleOutput.substring(consoleOutput.lastIndexOf("format:")).contains(Convert.toHexString("Secret message here!"
                    .getBytes())));
        }
        finally {
            System.setOut(systemOut);
            System.out.println(new String(out.toByteArray()));
            out.close();
        }
    }


    @Test
    public void testSplittedDecryption() throws Exception {
        PrintStream systemOut = System.out;
        ByteArrayOutputStream out = new ByteArrayOutputStream(1024 * 1024);
        try {
            PrintStream printStream = new PrintStream(out);
            System.setOut(printStream);
            String[] params = new String[] {
                    ""
                    + ";" +
""
                    , "certs/2_2.crt"
                    , "false"
            };

            RSADecryption.main(params);
            printStream.close();
            String consoleOutput = new String(out.toByteArray());
            Assert.assertTrue(consoleOutput.substring(consoleOutput.lastIndexOf("format:")).contains(""));
        }
        finally {
            System.setOut(systemOut);
            System.out.println(new String(out.toByteArray()));
            out.close();
        }
    }

    @Test
    public void testDoubleDecryption() throws Exception {
        PrintStream systemOut = System.out;
        ByteArrayOutputStream out = new ByteArrayOutputStream(1024 * 1024);
        try {
            PrintStream printStream = new PrintStream(out);
            System.setOut(printStream);
            String[] params = new String[] {
                    ""
                            + ";" +
                            ""
                    , "certs/2_2.crt;certs/1_2.crt"
                    , "false"
            };

            RSADecryption.main(params);
            printStream.close();
            String consoleOutput = new String(out.toByteArray());
            Assert.assertTrue(consoleOutput.substring(consoleOutput.lastIndexOf("format:")).contains(Convert.toHexString("Secret message here!"
                    .getBytes())));
        }
        finally {
            System.setOut(systemOut);
            System.out.println(new String(out.toByteArray()));
            out.close();
        }
    }
}
